insert into habit (created_at, last_modified_at, auth_end_time, auth_start_time, auth_type, avg_score, body, body_html, challengers, fail_img_url, sub_title, succ_img_url, thumb_img_url, title, category_id, user_id) values ('2022-09-23 11:41:37', '2023-02-03 12:11:03', '23:59:00', '00:00:00', '카메라', '4.8', '하루에 한 번 거북목 방지 운동을해요\n\n현대인은 책상앞에 너무 오래 앉아있어서 거북목이 많아요\n\n어깨가 결리니깐 매일 방지운동을 해요\n', '<p>하루에 한 번 거북목 방지 운동을해요</p><p><br></p><p>현대인은 책상앞에 너무 오래 앉아있어서 거북목이 많아요</p><p><br></p><p>어깨가 결리니깐 매일 방지운동을 해요</p>', '20', 'http://challenge66.file.bucket.s3.ap-northeast-2.amazonaws.com/images/77651807-f8bd-4dd5-ab99-4613ca90231a.jpeg', '거북목 방지', 'http://challenge66.file.bucket.s3.ap-northeast-2.amazonaws.com/images/7d40d909-482c-415c-9fa1-0403c2db0d2f.jpeg', 'http://challenge66.file.bucket.s3.ap-northeast-2.amazonaws.com/images/fc39abf3-683b-44c3-a395-7d2276a17934.png', '하루에 한 번 거북목 방지 운동', '1', '12');
insert into habit (created_at, last_modified_at, auth_end_time, auth_start_time, auth_type, avg_score, body, body_html, challengers, fail_img_url, sub_title, succ_img_url, thumb_img_url, title, category_id, user_id) values ('2022-09-23 11:41:37', '2023-02-03 12:14:47', '23:59:00', '00:00:00', '카메라', '4.3', '매일 1끼 샐러드로 가볍고 건강한 식사를 해요!\n샐러드는 균형 잡힌 좋은 식사입니다. 다양한 토핑과 소스로 커스터마이징이 가능할 뿐만 아니라, 충분한 야채를 섭취할 수 있죠.\n	\n단, 개인 취향이지만 샐러드 소스는 적당량 섭취하시는 것을 권장드립니다.\n', '<p>매일 1끼 샐러드로 가볍고 건강한 식사를 해요!</p><p>샐러드는 균형 잡힌 좋은 식사입니다. 다양한 토핑과 소스로 커스터마이징이 가능할 뿐만 아니라, 충분한 야채를 섭취할 수 있죠.</p><p>	</p><p>단, 개인 취향이지만 샐러드 소스는 적당량 섭취하시는 것을 권장드립니다.</p>', '15', 'http://challenge66.file.bucket.s3.ap-northeast-2.amazonaws.com/images/8cf1b148-4c82-45c6-9de7-c776928063c9.png', '샐러드 1끼', 'http://challenge66.file.bucket.s3.ap-northeast-2.amazonaws.com/images/e59be514-92c3-4db1-9982-323ec7e1affd.png', 'http://challenge66.file.bucket.s3.ap-northeast-2.amazonaws.com/images/3e0a1a4d-3792-42d1-959f-ee58daa82d4e.png', '매일 한끼 샐러드 먹기', '2', '13');
insert into habit (created_at, last_modified_at, auth_end_time, auth_start_time, auth_type, avg_score, body, body_html, challengers, fail_img_url, sub_title, succ_img_url, thumb_img_url, title, category_id, user_id) values ('2022-09-23 11:41:37', '2023-02-03 12:26:02', '19:00:00', '15:00:00', '카메라', '4', '저녁 식사를 19시 이전에 해보아요.\n너무 늦은 저녁은 야식과 마찬가지랍니다!\n우리의 몸 건강을 위해 조금 이른 저녁식사를 합시다.\n', '<p>저녁 식사를 19시 이전에 해보아요.</p><p>너무 늦은 저녁은 야식과 마찬가지랍니다!</p><p>우리의 몸 건강을 위해 조금 이른 저녁식사를 합시다.</p>', '35', 'http://challenge66.file.bucket.s3.ap-northeast-2.amazonaws.com/images/bdb41d3a-f87a-47b4-b9ec-480759d01ea3.jpeg', '저녁 19시', 'http://challenge66.file.bucket.s3.ap-northeast-2.amazonaws.com/images/9d3f4866-188d-428e-8d44-535c794497a4.jpeg', 'http://challenge66.file.bucket.s3.ap-northeast-2.amazonaws.com/images/13dafbc3-ab58-42df-bc32-25ac90270dad.jpeg', '매일 저녁 식사 19시 전에 하기', '2', '14');
